import React, { useState } from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration";

function TimerDurations({ TimerData, isTimerRunning, setTimerData }) {
  const handleInOrDe = (event) => {
    event.preventDefault();
    const whichButton =
      event.target.nodeName === "SPAN"
        ? event.target.parentNode.name
        : event.target.name;
    console.log(whichButton);
    switch (whichButton) {
      case "decrease-focus":
        setTimerData((currentData) => Math.max(currentData.focusTime - 1, 1));
        break;
      case "increase-focus":
        setTimerData((currentData) => Math.min(currentData.focusTime + 1, 15));
        break;
      case "decrease-break":
        setTimerData((currentData) => Math.max(currentData.breakTime - 5, 5));
        break;
      case "increase-break":
        setTimerData((currentData) => Math.min(currentData.breakTime + 5, 60));
        break;
      default:
        break;
    }
  };
  return (
    <div className="row">
      <div className="col">
        <div className="input-group input-group-lg mb-2">
          <span className="input-group-text" data-testid="duration-focus">
            {/* TODO: Update this text to display the current focus session duration */}
            Focus Duration: {minutesToDuration(TimerData.focusTime)}
          </span>
          <div className="input-group-append">
            {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="decrease-focus"
              disabled={isTimerRunning}
              onClick={handleInOrDe}
              name="decrease-focus"
            >
              <span className="oi oi-minus" />
            </button>
            {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="increase-focus"
              disabled={isTimerRunning}
              onClick={handleInOrDe}
              name="increase-focus"
            >
              <span className="oi oi-plus" />
            </button>
          </div>
        </div>
      </div>
      <div className="col">
        <div className="float-right">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-break">
              {/* TODO: Update this text to display the current break session duration */}
              Break Duration: {minutesToDuration(TimerData.breakTime)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-break"
                disabled={isTimerRunning}
                onClick={handleInOrDe}
                name="decrease-break"
              >
                <span className="oi oi-minus" name="decrease-break" />
              </button>
              {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-break"
                disabled={isTimerRunning}
                onClick={handleInOrDe}
                name="increase-break"
              >
                <span className="oi oi-plus" name="increase-break" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TimerDurations;
